import React, {useState, preventDefault} from "react";

import Header from "./components/Layout/Header";
import Meals from "./components/Meals/Meals";
import Cart from "./components/Cart/Cart";

function App() {
  const [cart, setOpenCart] = useState(false);

  const openCartHandler = () => {
    preventDefault();
    //setOpenCart(true);
    console.log('clicked')
  };

  const closeCartHandler = () => {
    preventDefault();
    //setOpenCart(false);
    console.log('Close clicked')
  };

  return (
    <React.Fragment>
      <Cart onClick={closeCartHandler}/>
      <Header onClick={openCartHandler}/>
      <main>
        <Meals />
      </main>
    </React.Fragment>
  );
}

export default App;
